var e="https://cowswap.exchange/schemas/app-data/v0.7.0.json",t="http://json-schema.org/draft-07/schema",r="Metadata JSON document for adding information to orders.",i=["version","metadata"],s="AppData Root Schema",a="object",o={version:{readOnly:!0,default:"0.7.0",$id:"#/definitions/version",description:"Semantic versioning of document.",examples:["1.0.0","1.2.3"],title:"Version",type:"string"},appCode:{$id:"#/properties/appCode",description:"The code identifying the CLI, UI, service generating the order.",examples:["CoW Swap"],title:"App Code",type:"string"},environment:{$id:"#/properties/environment",description:"Environment from which the order came from.",title:"Environment",type:"string",examples:["production","development","staging","ens"]},metadata:{$id:"#/properties/metadata",default:{},description:"Each metadata will specify one aspect of the order.",required:[],title:"Metadata",type:"object",properties:{referrer:{$id:"#referrer/v0.1.0.json",$schema:"http://json-schema.org/draft-07/schema",required:["version","address"],title:"Referrer",type:"object",properties:{version:{$ref:"#/properties/version",readOnly:!0,default:"0.1.0"},address:{title:"Referrer address",$id:"#/definitions/ethereumAddress",pattern:"^0x[a-fA-F0-9]{40}$",examples:["0xb6BAd41ae76A11D10f7b0E664C5007b908bC77C9"],type:"string"}}},utm:{$id:"#utm/v0.1.0.json",$schema:"http://json-schema.org/draft-07/schema",required:[],title:"UTM Codes",type:"object",properties:{utmSource:{$id:"#/properties/utmSource",title:"UTM Source",type:"string",examples:["twitter"],description:"Tracks in which medium the traffic originated from (twitter, facebook, etc.)"},utmMedium:{$id:"#/properties/utmMedium",title:"UTM Medium",type:"string",examples:["email"],description:"Tracks in which medium the traffic originated from (mail, CPC, social, etc.)"},utmCampaign:{$id:"#/properties/utmCampagin",title:"UTM Campaign",type:"string",examples:["everyone-loves-cows-2023"],description:"Track the performance of a specific campaign"},utmContent:{$id:"#/properties/utmContent",title:"UTM Content",type:"string",examples:["big-fat-button"],description:"Track which link was clicked"},utmTerm:{$id:"#/properties/utmTem",title:"UTM Keyword Term",type:"string",examples:["coincidence+of+wants"],description:"Track which keyword term a website visitor came from"}}},quote:{$id:"#quote/v0.2.0.json",$schema:"http://json-schema.org/draft-07/schema",required:["version","slippageBips"],title:"Quote",type:"object",properties:{version:{$ref:"#/properties/version",readOnly:!0,default:"0.2.0"},slippageBips:{$id:"#/properties/slippageBips",title:"Slippage Bips",description:"Slippage tolerance that was applied to the order to get the limit price. Expressed in Basis Points (BPS)",examples:["5","10","20","100"],pattern:"^\\d+(\\.\\d+)?$",type:"string"}}},orderClass:{$id:"#orderClass/v0.2.0.json",$schema:"http://json-schema.org/draft-07/schema",required:["version","orderClass"],title:"Order class",type:"object",properties:{version:{$ref:"#/properties/version",readOnly:!0,default:"0.2.0"},orderClass:{$id:"#/definitions/orderClass",description:"Indicator of the order class.",examples:["market","limit","liquidity","twap"],title:"Order class",type:"string",enum:["market","limit","liquidity","twap"]}}}}}},p={$id:e,$schema:t,description:r,required:i,title:s,type:a,properties:o};export{e as $id,t as $schema,p as default,r as description,o as properties,i as required,s as title,a as type};
